import { useState } from "react";
import styles from "./RegisterPage.module.css";
//import { useNavigate } from "react-router-dom";
import axios from "../lib/axios";

function RegisterPage() {
  //초기 values 설정
  const [values, setValues] = useState({
    name: "",
    email: "",
    password: "",
    passwordRepeat: "",
  });

  //const navigate = useNavigate();

  function handleChange(e) {
    const { name, value } = e.target;

    setValues((prevValues) => ({
      ...prevValues,
      [name]: value,
    }));
  }

  async function handleSubmit(e) {
    e.preventDefault();

    //비밀번호와 비밀번호확인값이 같은지 확인
    if (values.password !== values.passwordRepeat) {
      alert("비밀번호가 일치하지 않습니다");
      return;
    }
    const { name, email, password, passwordRepeat } = values;
    try {
      const response = await axios.post(
        "/member/register",
        { name, email, password, passwordRepeat },
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      console.log(response.data); // 성공했을 경우 응답 데이터
    } catch (error) {
      console.error("Error during request:", error);
    }

    //await axios.post("/auth/login", { email, password }); //회원가입 후 로그인 하기
  }

  //카카오로그인
  const handleKakaoLoginClick = () => {
    window.location.href = "http://localhost:8080/oauth/kakao"; //페이지 리다이렉트
  };

  return (
    <>
      <div className={styles.loginPage}>
        <h1 className={styles.header}>회원가입 &#128075;</h1>
        <section className={styles.formLogin}>
          <form onSubmit={handleSubmit}>
            <input
              id="name"
              name="name"
              type="text"
              placeholder="이름"
              value={values.name}
              onChange={handleChange}
            />
            <input
              id="email"
              name="email"
              type="email"
              placeholder="이메일"
              value={values.email}
              onChange={handleChange}
            />
            <input
              id="password"
              name="password"
              type="password"
              placeholder="비밀번호"
              value={values.password}
              onChange={handleChange}
            />
            <input
              id="passwordRepeat"
              name="passwordRepeat"
              type="password"
              placeholder="비밀번호 확인"
              value={values.passwordRepeat}
              onChange={handleChange}
            />
            <button type="submit">회원가입</button>
          </form>
        </section>
        <section className={styles.simpleLogin}>
          <img
            alt="loginImg"
            src={require(`../assets/kakaoLoginButton.png`)}
            onClick={handleKakaoLoginClick}
          />
          <img alt="loginImg" src={require(`../assets/naverLoginButton.png`)} />
        </section>
      </div>
    </>
  );
}

export default RegisterPage;
